import { useState } from 'react';
import React from "react";
import ReactDOM from 'react-dom/client';


const Maindiv=()=>
{

    const [data, setdata]=useState(
        {
            title:'',
            note:'',
        }
     );
        const getInput=(inp)=>
        {

            const name=inp.target.name
            const value=inp.target.value

            alert(name);
            alert(value);

            setdata((prevVal)=>
            {
                return{
                ...prevVal,
               
                }
            }

        )
        }
        
   


    return(
    <div className="center-wrapper">
  <div className="note-card">
    <input name='title' type="text" value={data.title} placeholder="Title" onChange={getInput} />
    <textarea name='note' value={data.note} placeholder="Write a note..." onChange={getInput} />
    <button className="btn btn-primary plus-btn">+</button>
  </div>
</div>)
}

export default Maindiv
