import React, { useState } from "react";
import './styl.css';
import Maindiv from "./Maindiv";
import Seconddiv from "./Seconddiv";

function App() {
  const [notes, setNotes] = useState([]);

  const func = (data) => {
    setNotes((prev) => [...prev, data]); // add new note
  };

  const handleDelete = (index) => {
    setNotes((prev) => prev.filter((_, i) => i !== index));
  };

  return (
    <>
      {/* First card â†’ centered */}
      <Maindiv pn={func} />

      {/* Display notes in Seconddiv */}
      {notes.map((n, index) => (
        <Seconddiv
          key={index}
          title={n.title}
          note={n.note}
          onDelete={() => handleDelete(index)}
        />
      ))}
    </>
  );
}

export default App;
